import { Feature } from '@turf/helpers';
import { DendroLineString, DendroPoint, DendroPolygon, Transform } from './dendrogram';
export type DagSection = DendroPolygon | DendroLineString | DendroPoint;
export type DagSectionsWithCategory = Record<NodeHandle['id'], DagSection[]>;
export type NodeHandle = {
    id: string;
    label?: string;
    order?: number;
    geoTypes?: Feature['geometry']['type'][];
};
export type Node = {
    id: string;
    transform?: Transform;
    userDefinedCoordinates?: [number, number];
    inputHandles?: NodeHandle[];
    outputHandles?: NodeHandle[];
    properties?: Feature['properties'];
};
export type Edge = {
    sourceNodeId?: string;
    sourceHandleId?: string;
    destinationNodeId?: string;
    destinationHandleId?: string;
};
export type Dag = {
    nodes: Node[];
    edges: Edge[];
    version: string;
};
export type NodeCalcValues = {
    outputSectionsWithCategory: DagSectionsWithCategory;
    inputSectionsWithCategory?: DagSectionsWithCategory;
    dependencies: Node['id'][];
    dependencyFeatureIds: Feature['id'][];
    nodeCache?: Node;
    isChanged?: boolean;
};
export type NodeCalcResult = Record<Node['id'], NodeCalcValues>;
