/**
 * Shortcut: used for String.includes() in content-type header
 */
export type FileContentType = 'image/png' | 'image/jpeg' | 'image/webp' | 'image/*' | 'model/glb' | 'model/gltf' | 'application/json' | 'application/pdf';
export type FileTypeSubstring = 'image' | 'model' | 'document' | 'geo';
export interface GiFile {
    id: string;
    name: string;
    url: string;
    org: number;
    org_name: string;
    headers: {
        contentType: FileContentType;
    };
    size: number;
}
export type UploadedFile = Pick<GiFile, 'headers' | 'name' | 'url'>;
export type UploadedFileResponse = {
    bucket: string;
    storage_path: string;
    url: string;
};
export type UploadedFileDetails = {
    url: string;
    bucket: string;
    content_type: string;
    modelPath: string;
    storage_path: string;
    extension?: string;
};
export type ProjectAttachment = {
    id: number;
    name: string;
    project: number;
    file_uploads: UploadedFileDetails[];
    url: string;
    tags?: {
        app: string;
        appName: string;
        meta: Record<string, any>;
    }[];
};
