export type ModelToAccess = 'project' | 'layer' | 'file' | 'kanban';
export type AccessEntity = 'user' | 'team' | 'org';
export type AccessLevel = 'none' | 'view' | 'edit' | 'admin';
export type OrgModelAccess<Model extends ModelToAccess = ModelToAccess> = {
    [model in Model]: number | string;
} & {
    id: number | string;
    org: number | string;
    org_name?: string;
    access_level: AccessLevel;
};
export type TeamModelAccess<Model extends ModelToAccess = ModelToAccess> = {
    [model in Model]: number | string;
} & {
    id: number | string;
    team: number | string;
    team_name: string;
    access_level: AccessLevel;
};
export type UserModelAccess<Model extends ModelToAccess = ModelToAccess> = {
    [model in Model]: number | string;
} & {
    access_level: AccessLevel;
    id: number | string;
    user_email: string;
};
export declare const getNiceAccessVerb: (access: AccessLevel) => string;
export declare const assignableAccessLevels: AccessLevel[];
export type ModelEndpoint<Entity extends AccessEntity, Model extends ModelToAccess> = `${Entity}${Model}s`;
export type ModelAccessMap = {
    user: UserModelAccess;
    team: TeamModelAccess;
    org: OrgModelAccess;
};
export declare const getAccessModelEndpoint: <Entity extends AccessEntity, Model extends ModelToAccess>(entity: Entity, model: Model) => `${Entity}${Model}s`;
